import React, { useEffect, useRef, useState } from "react";
import {
  Box,
  Card,
  CardHeader,
  IconButton,
  Modal,
  Paper,
} from "@mui/material";
import CreateIcon from "@mui/icons-material/Create";
import { useDispatch, useSelector } from "react-redux";
import CreateCategoryParent from "./CreateCategoryParent";
import { getCategory } from "../../componet/State/CategoryTest/Action";
import $ from "jquery";
import "datatables.net";
import "datatables.net-dt/css/jquery.dataTables.css";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

export default function CategoryParentTable() {
  const [open, setOpen] = useState(false);
  const tableRef = useRef(null); // Tham chiếu đến bảng
  const dispatch = useDispatch();
  const { restaurant, category } = useSelector((store) => store);
  const jwt = localStorage.getItem("jwt");

  useEffect(() => {
    dispatch(getCategory({ id: restaurant.usersRestaurant.id, jwt }));
  }, [dispatch, restaurant.usersRestaurant.id, jwt]);

  useEffect(() => {
    if (category.category.length > 0) {
      // Khởi tạo DataTable sau khi dữ liệu được load
      $(tableRef.current).DataTable();
    }
  }, [category.category]);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div>
      <Box>
        <Card className="mt-1">
          <CardHeader
            action={
              <IconButton onClick={handleOpen} aria-label="settings">
                <CreateIcon />
              </IconButton>
            }
            title={"Danh mục thành phần"}
            sx={{ pt: 2, alignItems: "center" }}
          />

          {/* DataTable */}
          <Paper>
            <table ref={tableRef} className="display" style={{ width: "100%" }}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Tên</th>
                </tr>
              </thead>
              <tbody>
                {category.category.map((item) => (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </Paper>
        </Card>

        {/* Modal */}
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <CreateCategoryParent />
          </Box>
        </Modal>
      </Box>
    </div>
  );
}
