import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Pagination,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    TextField,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
} from '@mui/material';
import { fetchBlogs } from '../../componet/State/Blog/Action';

// Hàm lấy JWT từ localStorage
const getAuthHeader = () => {
    const jwt = localStorage.getItem('jwt');
    return {
        headers: {
            Authorization: `Bearer ${jwt}`,
        },
    };
};

const BlogManagement = () => {
    const dispatch = useDispatch();
    const { blogs, loading, error, currentPage, totalPages } = useSelector((state) => state.blog);

    const [page, setPage] = useState(1); // Trang hiện tại (bắt đầu từ 1)
    const [openDialog, setOpenDialog] = useState(false);
    const [isEditMode, setIsEditMode] = useState(false);
    const [editingBlog, setEditingBlog] = useState({
        id: null,
        title: '',
        content: '',
        category: '',
        images: [],
    });
    const [selectedImages, setSelectedImages] = useState([]);
    const [selectedFilterCategory, setSelectedFilterCategory] = useState('');

    const categories = ['Tài liệu học tập', 'Hướng dẫn thực hành', 'Khác'];

    // Fetch Blogs mỗi khi page thay đổi
    useEffect(() => {
        dispatch(fetchBlogs({ page: page - 1, size: 5 }, getAuthHeader())); // Backend page bắt đầu từ 0
    }, [dispatch, page]);

    // Lọc Blog theo danh mục
    const filteredBlogs = selectedFilterCategory
        ? blogs.filter((blog) => blog.category === selectedFilterCategory)
        : blogs;

    const handlePageChange = (event, value) => {
        setPage(value);
    };

    const handleAddOrUpdateBlog = () => {
        const updatedBlog = {
            ...editingBlog,
            images: selectedImages.map((file) => URL.createObjectURL(file)),
        };

        if (isEditMode) {
            // Dispatch API cập nhật blog (đính kèm getAuthHeader)
        } else {
            // Dispatch API thêm mới blog (đính kèm getAuthHeader)
        }

        setEditingBlog({ id: null, title: '', content: '', category: '', images: [] });
        setSelectedImages([]);
        setOpenDialog(false);
        setIsEditMode(false);
    };

    const handleOpenDialog = (blog = null) => {
        if (blog) {
            setEditingBlog(blog);
            setSelectedImages([]);
            setIsEditMode(true);
        } else {
            setEditingBlog({ title: '', content: '', category: '', images: [] });
            setSelectedImages([]);
            setIsEditMode(false);
        }
        setOpenDialog(true);
    };

    const handleDeleteBlog = (id) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa bài viết này?')) {
            // Dispatch API xóa blog (đính kèm getAuthHeader)
        }
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h1 style={{ textAlign: 'center' }}>Quản lý Blog</h1>

            {loading && <p>Đang tải dữ liệu...</p>}
            {error && <p style={{ color: 'red' }}>Lỗi: {error}</p>}

            {/* Lọc & Thêm mới */}
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
                <FormControl style={{ minWidth: 200 }}>
                    <InputLabel>Lọc theo danh mục</InputLabel>
                    <Select
                        value={selectedFilterCategory}
                        onChange={(e) => setSelectedFilterCategory(e.target.value)}
                    >
                        <MenuItem value="">Tất cả</MenuItem>
                        {categories.map((category) => (
                            <MenuItem key={category} value={category}>
                                {category}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
                <Button variant="contained" onClick={() => handleOpenDialog()}>
                    Thêm Mới
                </Button>
            </div>

            {/* Bảng hiển thị Blogs */}
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Tiêu đề</TableCell>
                            <TableCell>Danh mục</TableCell>
                            <TableCell>Ngày tạo</TableCell>
                            <TableCell>Ảnh</TableCell>
                            <TableCell>Hành động</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredBlogs.map((blog) => (
                            <TableRow key={blog.id}>
                                <TableCell>{blog.title}</TableCell>
                                <TableCell>{blog.category}</TableCell>
                                <TableCell>{blog.createdAt}</TableCell>
                                <TableCell>
                                    {blog.images.map((image, index) => (
                                        <img
                                            key={index}
                                            src={image}
                                            alt="blog"
                                            style={{ width: '50px', height: '50px', marginRight: '5px' }}
                                        />
                                    ))}
                                </TableCell>
                                <TableCell>
                                    <Button onClick={() => handleOpenDialog(blog)}>Sửa</Button>
                                    <Button onClick={() => handleDeleteBlog(blog.id)}>Xóa</Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {/* Phân trang */}
            <Pagination
                count={totalPages}
                page={page}
                onChange={handlePageChange}
                style={{ marginTop: '1rem', display: 'flex', justifyContent: 'center' }}
            />

            {/* Dialog thêm/sửa */}
            <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
                <DialogTitle>{isEditMode ? 'Chỉnh sửa Blog' : 'Thêm Blog mới'}</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Tiêu đề"
                        fullWidth
                        value={editingBlog.title}
                        onChange={(e) => setEditingBlog({ ...editingBlog, title: e.target.value })}
                    />
                    <TextField
                        label="Nội dung"
                        fullWidth
                        multiline
                        rows={4}
                        value={editingBlog.content}
                        onChange={(e) => setEditingBlog({ ...editingBlog, content: e.target.value })}
                    />
                    <FormControl fullWidth>
                        <InputLabel>Danh mục</InputLabel>
                        <Select
                            value={editingBlog.category}
                            onChange={(e) => setEditingBlog({ ...editingBlog, category: e.target.value })}
                        >
                            {categories.map((category) => (
                                <MenuItem key={category} value={category}>
                                    {category}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
                    <Button onClick={handleAddOrUpdateBlog}>{isEditMode ? 'Cập nhật' : 'Thêm'}</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default BlogManagement;
