import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getBlogsPaginated } from './blogActions'; // Import action lấy danh sách blog
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Pagination,
} from '@mui/material';

const BlogManagement = () => {
    const dispatch = useDispatch();
    const { blogs, loading, error, currentPage, totalPages } = useSelector((state) => state.blog);

    const [page, setPage] = useState(1); // Trang hiện tại (bắt đầu từ 1)

    // Fetch Blogs mỗi khi page thay đổi
    useEffect(() => {
        dispatch(getBlogsPaginated(page - 1, 5)); // Backend page bắt đầu từ 0
    }, [dispatch, page]);

    const handlePageChange = (event, value) => {
        setPage(value);
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h1 style={{ textAlign: 'center' }}>Quản lý Blog</h1>
            {loading && <p>Đang tải...</p>}
            {error && <p style={{ color: 'red' }}>Lỗi: {error}</p>}

            <TableContainer component={Paper} style={{ marginTop: '1rem' }}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Tiêu đề</TableCell>
                            <TableCell>Danh mục</TableCell>
                            <TableCell>Ngày tạo</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {blogs.map((blog) => (
                            <TableRow key={blog.id}>
                                <TableCell>{blog.title}</TableCell>
                                <TableCell>{blog.category}</TableCell>
                                <TableCell>{blog.createdAt}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {/* Phân Trang */}
            <Pagination
                count={totalPages}
                page={page}
                onChange={handlePageChange}
                color="primary"
                style={{ marginTop: '1rem', display: 'flex', justifyContent: 'center' }}
            />
        </div>
    );
};

export default BlogManagement;
