import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCategory } from "../../componet/State/CategoryTest/Action";
import DataTable from 'datatables.net-react';
import DT from 'datatables.net-dt';
import { Box, Card, CardHeader, IconButton, Modal } from "@mui/material";
import CreateIcon from "@mui/icons-material/Create";
import CreateCategoryParent from "./CreateCategoryParent";
//import 'datatables.net-dt/css/jquery.dataTables.css';

DataTable.use(DT);

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

export default function CategoryParentTable() {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const dispatch = useDispatch();
  const { restaurant, category } = useSelector((store) => store);
  const jwt = localStorage.getItem("jwt");

  useEffect(() => {
    dispatch(getCategory({ id: restaurant.usersRestaurant.id, jwt }));
  }, [dispatch, restaurant.usersRestaurant.id, jwt]);

  const tableData = category.category.map((item) => [item.id, item.name]);

  return (
    <div>
      <Box>
        <Card className="mt-1">
          <CardHeader
            action={
              <IconButton onClick={handleOpen} aria-label="settings">
                <CreateIcon />
              </IconButton>
            }
            title={"Danh mục thành phần"}
            sx={{ pt: 2, alignItems: "center" }}
          />

          <DataTable data={tableData} className="display">
            <thead>
              <tr>
                <th>ID</th>
                <th>Tên</th>
              </tr>
            </thead>
          </DataTable>
        </Card>
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <CreateCategoryParent />
          </Box>
        </Modal>
      </Box>
    </div>
  );
}
