import React, { useEffect, useState } from 'react';
import {
  Box,
  Card,
  TableContainer,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Typography,
  Paper,
  Chip,
  Button,
  Stack,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  Menu,
  MenuItem,
} from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { getOrders, getOrderById, updateOrderStatus } from '../../componet/State/Order/Action';

const paymentTypes = ['Tiền mặt', 'Chuyển khoản', 'Thẻ tín dụng', 'Ví điện tử'];
const paymentStatuses = ['Chưa thanh toán', 'Đã thanh toán'];
const statusOptions = ['Chưa giải quyết', 'Đang giao hàng', 'Đã nhận hàng', 'Đã hoàn thành đơn'];

const Orders = () => {
  const dispatch = useDispatch();
  const {
    orders,
    page,
    size,
    totalPages,
    loading,
    error,
    selectedOrder,
    orderLoading,
    updating,
    updateSuccess,
    updateError,
  } = useSelector((state) => state.order);

  const jwt = localStorage.getItem('jwt');
  const [currentPage, setCurrentPage] = useState(0);
  const [visiblePages, setVisiblePages] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null); // Menu trạng thái
  const [currentStatus, setCurrentStatus] = useState('Chưa giải quyết'); // Trạng thái hiện tại

  useEffect(() => {
    if (jwt) {
      dispatch(getOrders(jwt, currentPage, 10));
    }
  }, [dispatch, jwt, currentPage]);

  useEffect(() => {
    updateVisiblePages();
  }, [currentPage, totalPages]);

  useEffect(() => {
    if (updateSuccess) {
      alert('Cập nhật trạng thái thành công!');
      setAnchorEl(null);
    }
    if (updateError) {
      alert(`Lỗi: ${updateError}`);
    }
  }, [updateSuccess, updateError]);

  const updateVisiblePages = () => {
    const maxVisible = 3;
    let startPage = Math.max(0, currentPage - 1);
    let endPage = Math.min(totalPages - 1, startPage + maxVisible - 1);

    if (endPage - startPage + 1 < maxVisible) {
      startPage = Math.max(0, endPage - maxVisible + 1);
    }

    const pages = [];
    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    setVisiblePages(pages);
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  const handleOpenModal = (orderId) => {
    dispatch(getOrderById(jwt, orderId)); // Gọi API lấy chi tiết đơn hàng
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
  };

  const handleStatusClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleStatusSelect = (newStatus) => {
    if (selectedOrder) {
      dispatch(updateOrderStatus(jwt, selectedOrder.id, newStatus));
      setCurrentStatus(newStatus);
      setAnchorEl(null);
    }
  };

  return (
    <Box p={3}>
      <Card>
        <Typography variant="h5" sx={{ p: 2, textAlign: 'center' }}>
          📦 Quản lý Đơn Hàng
        </Typography>

        {/* Loading */}
        {loading && (
          <Stack direction="row" justifyContent="center" alignItems="center" sx={{ p: 4 }}>
            <CircularProgress />
          </Stack>
        )}

        {/* Error */}
        {error && (
          <Typography variant="h6" color="error" align="center" sx={{ p: 2 }}>
            ❌ {error}
          </Typography>
        )}

        {/* Danh sách đơn hàng */}
        {!loading && !error && (
          <>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>#</TableCell>
                    <TableCell>Trạng thái</TableCell>
                    <TableCell>Ngày tạo</TableCell>
                    <TableCell>Phương thức thanh toán</TableCell>
                    <TableCell>Trạng thái thanh toán</TableCell>
                    <TableCell>Tổng số món</TableCell>
                    <TableCell>Tổng giá</TableCell>
                    <TableCell>Chi tiết</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {orders.map((order) => (
                    <TableRow key={order.id} hover>
                      <TableCell>{order.id}</TableCell>
                      <TableCell>
                        <Chip label={order.orderStatus} color="primary" />
                      </TableCell>
                      <TableCell>{new Date(order.createAt).toLocaleString()}</TableCell>
                      <TableCell>{paymentTypes[order.paymentType]}</TableCell>
                      <TableCell>{paymentStatuses[order.statusPayment]}</TableCell>
                      <TableCell>{order.totalItem}</TableCell>
                      <TableCell>{order.totalPrice.toLocaleString()} VND</TableCell>
                      <TableCell>
                        <Button variant="outlined" onClick={() => handleOpenModal(order.id)}>
                          Xem
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            {/* Phân trang */}
            <Stack direction="row" justifyContent="center" spacing={1} p={2}>
              <Button onClick={() => handlePageChange(currentPage - 1)}>←</Button>
              {visiblePages.map((pageNumber) => (
                <Button
                  key={pageNumber}
                  variant={currentPage === pageNumber ? 'contained' : 'outlined'}
                  onClick={() => handlePageChange(pageNumber)}
                >
                  {pageNumber + 1}
                </Button>
              ))}
              <Button onClick={() => handlePageChange(currentPage + 1)}>→</Button>
            </Stack>
          </>
        )}

        {/* Modal Chi Tiết Đơn Hàng */}
        <Dialog open={openModal} onClose={handleCloseModal} maxWidth="md" fullWidth>
          <DialogTitle>📋 Chi Tiết Đơn Hàng</DialogTitle>
          <DialogContent>
            <Typography>Trạng thái: {currentStatus}</Typography>
            <Button variant="outlined" onClick={handleStatusClick}>
              Thay đổi trạng thái
            </Button>
            <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={() => setAnchorEl(null)}>
              {statusOptions.map((status) => (
                <MenuItem key={status} onClick={() => handleStatusSelect(status)}>
                  {status}
                </MenuItem>
              ))}
            </Menu>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseModal}>Đóng</Button>
          </DialogActions>
        </Dialog>
      </Card>
    </Box>
  );
};

export default Orders;
