import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getOrders } from '../../componet/State/Order/Action';

const Orders = () => {
    const dispatch = useDispatch();
    const { orders, loading, error, page, totalPages } = useSelector((state) => state.orders);

    const jwt = localStorage.getItem('jwt');
    const [currentPage, setCurrentPage] = useState(0);

    useEffect(() => {
        if (jwt) {
            dispatch(getOrders(jwt, currentPage, 10));
        } else {
            console.error('Token không tồn tại. Vui lòng đăng nhập lại.');
        }
    }, [dispatch, jwt, currentPage]);

    const handlePageChange = (newPage) => {
        if (newPage >= 0 && newPage < totalPages) {
            setCurrentPage(newPage);
        }
    };

    if (!jwt) {
        return <p style={{ color: 'red' }}>Vui lòng đăng nhập để xem đơn hàng.</p>;
    }

    return (
        <div>
            <h2>Quản lý Đơn Hàng</h2>
            {loading && <p>Đang tải dữ liệu...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {orders?.map((order) => (
                    <li key={order.id}>
                        ID: {order.id}, Status: {order.orderStatus}, Price: {order.totalPrice} VND
                    </li>
                ))}
            </ul>
            <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 0}>
                ←
            </button>
            <span>
                Trang {currentPage + 1} / {totalPages}
            </span>
            <button
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages - 1}
            >
                →
            </button>
        </div>
    );
};

export default Orders;
