import React, { useEffect, useState } from "react";
import {
  Avatar,
  Box,
  Button,
  Card,
  CardHeader,
  IconButton,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
} from "@mui/material";
import CreateIcon from "@mui/icons-material/Create";
import { Delete } from "@mui/icons-material";
import { useDispatch, useSelector } from "react-redux";
import { getProductRestaurantId } from "../../componet/State/Product/Action";
import { getCategory, getCategoryItems } from "../../componet/State/CategoryTest/Action";

export default function ProductTable() {
  const dispatch = useDispatch();
  const jwt = localStorage.getItem("jwt");
  const { restaurant, product, category } = useSelector((store) => store);

  // State cho trang hiện tại
  const [currentPage, setCurrentPage] = useState(0);
  const [pageSize] = useState(10);

  // State cho popup
  const [open, setOpen] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    categoryItem: "",
    images: [],
    restaurantId: restaurant?.usersRestaurant?.id || null,
  });

  useEffect(() => {
    if (restaurant?.usersRestaurant?.id) {
      // Gọi API lấy sản phẩm
      dispatch(
        getProductRestaurantId({
          jwt,
          restaurantId: restaurant.usersRestaurant.id,
          page: currentPage,
          size: pageSize,
        })
      );

      // Gọi API lấy danh mục
      dispatch(
        getCategory({
          id: restaurant.usersRestaurant.id,
          jwt,
        })
      );
    }
  }, [dispatch, jwt, restaurant?.usersRestaurant?.id, currentPage, pageSize]);

  // Gọi API lấy danh mục con khi danh mục cha thay đổi
  useEffect(() => {
    if (formData.category) {
      dispatch(
        getCategoryItems({
          categoryId: formData.category,
          restaurantId: restaurant?.usersRestaurant?.id,
        })
      );
    }
  }, [dispatch, formData.category, restaurant?.usersRestaurant?.id]);

  // Xử lý thay đổi trang
  const handlePageChange = (page) => {
    if (page >= 0 && page < product.pagination.totalPages) {
      setCurrentPage(page);
    }
  };

  // Xử lý mở/đóng popup
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  // Xử lý thay đổi giá trị trong form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <Box>
      <Card className="mt-1">
        <CardHeader
          action={
            <IconButton onClick={handleOpen} aria-label="settings">
              <CreateIcon />
            </IconButton>
          }
          title={"Sản phẩm"}
          sx={{ pt: 2, alignItems: "center" }}
        />
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="left">Ảnh</TableCell>
                <TableCell align="right">Tên sản phẩm</TableCell>
                <TableCell align="right">Danh mục cha</TableCell>
                <TableCell align="right">Danh mục con</TableCell>
                <TableCell align="right">Giá</TableCell>
                <TableCell align="right">Trạng thái</TableCell>
                <TableCell align="right">Xóa</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {product.products.map((item) => (
                <TableRow
                  key={item.id}
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    <Avatar src={item.images[0]} />
                  </TableCell>
                  <TableCell align="right">{item.name}</TableCell>
                  <TableCell align="right">
                    {item.category ? item.category.name : "N/A"}
                  </TableCell>
                  <TableCell align="right">
                    {item.categoryItem ? (
                      Array.isArray(item.categoryItem) ? (
                        item.categoryItem.map((categoryItem) => (
                          <Chip
                            key={categoryItem.id}
                            label={categoryItem.name}
                          />
                        ))
                      ) : (
                        <Chip
                          key={item.categoryItem.id}
                          label={item.categoryItem.name}
                        />
                      )
                    ) : (
                      "N/A"
                    )}
                  </TableCell>
                  <TableCell align="right">{item.price} VND</TableCell>
                  <TableCell align="right">
                    <Button
                      variant="contained"
                      style={{
                        backgroundColor: item.available ? "green" : "red",
                      }}
                    >
                      {item.available ? "Còn Hàng" : "Hết Hàng"}
                    </Button>
                  </TableCell>
                  <TableCell align="right">
                    <IconButton color="primary">
                      <Delete />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Phân trang */}
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            mt: 2,
          }}
        >
          <Button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 0}
          >
            &lt;
          </Button>
          {Array.from({ length: 3 }, (_, i) => {
            const page = Math.max(0, currentPage - 1) + i;
            return page < product.pagination.totalPages ? (
              <Button
                key={page}
                onClick={() => handlePageChange(page)}
                variant={page === currentPage ? "contained" : "outlined"}
              >
                {page + 1}
              </Button>
            ) : null;
          })}
          <Button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage + 1 >= product.pagination.totalPages}
          >
            &gt;
          </Button>
        </Box>
      </Card>

      {/* Popup tạo sản phẩm */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Tạo sản phẩm</DialogTitle>
        <DialogContent>
          <TextField
            name="name"
            label="Tên sản phẩm"
            fullWidth
            margin="dense"
            value={formData.name}
            onChange={handleInputChange}
          />
          <TextField
            name="description"
            label="Mô tả"
            fullWidth
            margin="dense"
            value={formData.description}
            onChange={handleInputChange}
          />
          <TextField
            name="price"
            label="Giá"
            fullWidth
            margin="dense"
            type="number"
            value={formData.price}
            onChange={handleInputChange}
          />
          <TextField
            name="category"
            label="Danh mục cha"
            fullWidth
            margin="dense"
            select
            value={formData.category}
            onChange={handleInputChange}
          >
            {/* Hiển thị danh mục lấy từ API */}
            {category?.category?.map((cat) => (
              <MenuItem key={cat.id} value={cat.id}>
                {cat.name}
              </MenuItem>
            ))}
          </TextField>
          <TextField
              name="categoryItem"
              label="Danh mục con"
              fullWidth
              margin="dense"
              select
              value={formData.categoryItem}
              onChange={handleInputChange}
              disabled={!formData.category} // Vô hiệu hóa nếu chưa chọn danh mục cha
            >
              {/* Hiển thị danh mục con */}
              {category?.categoryItem?.length > 0 ? (
                category.categoryItem.map((item) => (
                  <MenuItem key={item.id} value={item.id}>
                    {item.name}
                  </MenuItem>
                ))
              ) : (
                <MenuItem value="" disabled>
                  {formData.category
                    ? "Không có danh mục con"
                    : "Vui lòng chọn danh mục cha trước"}
                </MenuItem>
              )}
            </TextField>
          <TextField
            name="images"
            label="Hình ảnh (URL, cách nhau bằng dấu phẩy)"
            fullWidth
            margin="dense"
            value={formData.images}
            onChange={(e) =>
              setFormData((prev) => ({
                ...prev,
                images: e.target.value.split(","),
              }))
            }
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Hủy</Button>
          <Button variant="contained" onClick={() => console.log(formData)}>
            Tạo
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
