import axios from "axios";
import { API_URL, api} from '../../config/api';
import { CREATE_CATEGORYITEM_SUCCESS, CREATE_CATEGORY_SUCCESS, CREATE_INGREDIENT_CATEGORY_SUCCESS, CREATE_INGREDIENT_SUCCESS, DELETE_CATEGORY_FAILURE, DELETE_CATEGORY_REQUEST, DELETE_CATEGORY_SUCCESS, GET_CATEGORYITEM, GET_CATEGORY_SUCCESS, GET_INGREDIENTS, GET_INGREDIENT_CATEGORY_SUCCESS, UPDATE_STOCK } from "./ActionType";

export const getCategoryItemOfRestaurant = ({id, jwt}) => {
    return async (dispatch) => {
        try {
            const response = await api.get(
                `/api/admin/category/restaurant/${id}`,
                {
                    headers:{
                        Authorization: `Bearer ${jwt}`,
                    },
                }
            );
            console.log("get all category item", response.data);
            dispatch({
                type:GET_CATEGORYITEM,
                payload:response.data,
            });
        } catch (error) {
            console.log("error", error);
        }
    };
};

export const createCategoryItem = ({data, jwt}) => {
    return async (dispatch) => {
        try {
            const response = await api.post(`/api/admin/category`, data, {
                headers: {
                    Authorization: `Bearer ${jwt}`,
                },
            });
            console.log("create categoryitem", response.data);
            dispatch({type:CREATE_CATEGORYITEM_SUCCESS, payload: response.data,});

        } catch (error) {
            console.log("error", error);
        }
    };
};

export const createCategory = ({data,jwt}) => {
    console.log("data", data, "jwt", jwt);
    return async (dispatch) => {
        try {
            const response = await api.post(`/api/admin/category/category`, data,{
                headers: {
                    Authorization: `Bearer ${jwt}`,
                },
            });
            console.log("create  category", response.data);
            dispatch({
                type:CREATE_CATEGORY_SUCCESS,
                payload: response.data,
            });
        } catch (error) {
            console.log("error", error);
        }
    };
};

export const getCategory = ({id, jwt}) => {
    return async (dispatch) => {
        try {
            const response = await api.get(
                `/api/admin/category/restaurant/${id}/category`,
                {
                    headers: {
                        Authorization: `Bearer ${jwt}`,
                    },
                }
            );
            console.log("get category", response.data);
            dispatch({
                type: GET_CATEGORY_SUCCESS,
                payload:response.data,
            });
        } catch (error) {
            console.log("error", error);
        }
    };
};

export const deleteCategory = ({ id, jwt }) => {
    return async (dispatch) => {
        try {
            // ðŸ“Œ Báº¯t Ä‘áº§u gá»­i yÃªu cáº§u xÃ³a
            dispatch({ type: DELETE_CATEGORY_REQUEST });

            const response = await api.delete(
                `/api/admin/category/${id}`,
                {
                    headers: {
                        Authorization: `Bearer ${jwt}`,
                    },
                }
            );

            console.log("delete category", response.data);

            // ðŸ“Œ XÃ³a thÃ nh cÃ´ng
            dispatch({
                type: DELETE_CATEGORY_SUCCESS,
                payload: id, // Gá»­i ID cá»§a category Ä‘Ã£ xÃ³a
            });

            // ðŸ“Œ LÃ m má»›i danh sÃ¡ch category
            dispatch({
                type: GET_CATEGORY_SUCCESS,
                payload: response.data,
            });

        } catch (error) {
            console.error("Error deleting category:", error);

            // ðŸ“Œ XÃ³a tháº¥t báº¡i
            dispatch({
                type: DELETE_CATEGORY_FAILURE,
                payload: error.message || "Failed to delete category",
            });
        }
    };
};
