import { GET_ORDERS_FAILURE, GET_ORDERS_REQUEST, GET_ORDERS_SUCCESS, GET_ORDERSID_FAILURE, GET_ORDERSID_REQUEST, GET_ORDERSID_SUCCESS, GET_USERS_ORDERS_FAILURE, GET_USERS_ORDERS_REQUEST, GET_USERS_ORDERS_SUCCESS } from "./ActionTypes";
const initialState = {
    orders: [],       // Danh sách đơn hàng
    loading: false,   // Trạng thái đang tải
    error: null,      // Lỗi nếu có
    page: 0,          // Trang hiện tại
    size: 10,         // Số phần tử trên mỗi trang
    totalElements: 0, // Tổng số phần tử
    totalPages: 0,    // Tổng số trang
    
};
export const orderReducer = (state = initialState, {type, payload}) => {
    switch(type){
        case GET_USERS_ORDERS_REQUEST:
            return {...state,error:null, loading:true};
        case GET_USERS_ORDERS_SUCCESS:
            return {...state, error:null, loading:false, orders:payload};
        case GET_USERS_ORDERS_FAILURE:
            return {...state, error:payload, loading:false};
        
            case GET_ORDERS_REQUEST:
                return {
                    ...state,
                    loading: true,
                    error: null,
                };
    
            case GET_ORDERS_SUCCESS:
                return {
                    ...state,
                    loading: false,
                    orders: payload.content, // Lấy danh sách đơn hàng từ payload
                    page: payload.pageable.pageNumber,
                    size: payload.pageable.pageSize,
                    totalElements: payload.totalElements,
                    totalPages: payload.totalPages,
                };
    
            case GET_ORDERS_FAILURE:
                return {
                    ...state,
                    loading: false,
                    error: payload,
                };

         // Lấy chi tiết đơn hàng theo ID
         case GET_ORDERSID_REQUEST:
            return {
                ...state,
                orderLoading: true,
                orderError: null,
                selectedOrder: null, // Reset chi tiết đơn hàng trước khi gọi API mới
            };

        case GET_ORDERSID_SUCCESS:
            return {
                ...state,
                orderLoading: false,
                selectedOrder: payload, // Lưu chi tiết đơn hàng từ API
                orderError: null,
            };

        case GET_ORDERSID_FAILURE:
            return {
                ...state,
                orderLoading: false,
                orderError: payload,
            };

        default:
            return state;
    }
};
