import React, { useEffect, useState } from 'react';
import {
  Avatar,
  Box,
  Button,
  Card,
  CardHeader,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  MenuItem,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
} from '@mui/material';
import { Delete } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getProductRestaurantId } from '../../componet/State/Product/Action';
import api from '../../api'; // Đảm bảo bạn đã có file API

export default function ProductTable() {
  const dispatch = useDispatch();
  const jwt = localStorage.getItem("jwt");
  const { restaurant, product } = useSelector((store) => store);
  const navigate = useNavigate();

  // State cho trang hiện tại
  const [currentPage, setCurrentPage] = useState(0);
  const [pageSize] = useState(10);

  // State cho Modal
  const [open, setOpen] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    categoryItem: '',
    images: '',
  });

  useEffect(() => {
    if (restaurant?.usersRestaurant?.id) {
      dispatch(getProductRestaurantId({
        jwt,
        restaurantId: restaurant.usersRestaurant.id,
        page: currentPage,
        size: pageSize,
      }));
    }
  }, [dispatch, jwt, restaurant?.usersRestaurant?.id, currentPage, pageSize]);

  const handlePageChange = (page) => {
    if (page >= 0 && page < product.pagination.totalPages) {
      setCurrentPage(page);
    }
  };

  // Xử lý mở/đóng modal
  const handleOpen = () => setOpen(true);
  const handleClose = () => {
    setOpen(false);
    setNewProduct({
      name: '',
      description: '',
      price: '',
      category: '',
      categoryItem: '',
      images: '',
    });
  };

  // Xử lý thay đổi input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({ ...prev, [name]: value }));
  };

  // Xử lý gửi dữ liệu sản phẩm
  const handleAddProduct = async () => {
    try {
      await api.post(
        '/api/admin/product/create',
        {
          ...newProduct,
          restaurantId: restaurant.usersRestaurant.id,
        },
        {
          headers: { Authorization: `Bearer ${jwt}` },
        }
      );
      handleClose();
      dispatch(getProductRestaurantId({
        jwt,
        restaurantId: restaurant.usersRestaurant.id,
        page: currentPage,
        size: pageSize,
      }));
    } catch (error) {
      console.error("Lỗi khi thêm sản phẩm:", error.response?.data || error.message);
    }
  };

  return (
    <Box>
      <Card className='mt-1'>
        <CardHeader
          action={
            <Button variant="contained" color="primary" onClick={handleOpen}>
              Thêm Sản Phẩm
            </Button>
          }
          title={"Sản phẩm"}
          sx={{ pt: 2, alignItems: "center" }}
        />
        
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="left">Ảnh</TableCell>
                <TableCell align="right">Tên sản phẩm</TableCell>
                <TableCell align="right">Danh mục cha</TableCell>
                <TableCell align="right">Danh mục con</TableCell>
                <TableCell align="right">Giá</TableCell>
                <TableCell align="right">Trạng thái</TableCell>
                <TableCell align="right">Xóa</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {product.products.map((item) => (
                <TableRow key={item.id}>
                  <TableCell>
                    <Avatar src={item.images?.[0]} />
                  </TableCell>
                  <TableCell align="right">{item.name}</TableCell>
                  <TableCell align="right">
                    {item.category ? item.category.name : 'N/A'}
                  </TableCell>
                  <TableCell align="right">
                    {item.categoryItem ? item.categoryItem.name : 'N/A'}
                  </TableCell>
                  <TableCell align="right">{item.price} VND</TableCell>
                  <TableCell align="right">
                    <Button
                      variant="contained"
                      style={{ backgroundColor: item.available ? 'green' : 'red' }}
                    >
                      {item.available ? "Còn Hàng" : "Hết Hàng"}
                    </Button>
                  </TableCell>
                  <TableCell align="right">
                    <Button color="error">
                      <Delete />
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Phân trang */}
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
          <Button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 0}>
            &lt;
          </Button>
          <Button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage + 1 >= product.pagination.totalPages}>
            &gt;
          </Button>
        </Box>
      </Card>

      {/* Popup Modal Thêm Sản Phẩm */}
      <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle>Thêm Sản Phẩm</DialogTitle>
        <DialogContent>
          <TextField name="name" label="Tên sản phẩm" fullWidth margin="normal" onChange={handleInputChange} />
          <TextField name="description" label="Mô tả" fullWidth margin="normal" onChange={handleInputChange} />
          <TextField name="price" label="Giá" fullWidth margin="normal" onChange={handleInputChange} />
          <TextField name="images" label="Ảnh (URL)" fullWidth margin="normal" onChange={handleInputChange} />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Hủy</Button>
          <Button variant="contained" color="primary" onClick={handleAddProduct}>Thêm</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
