import React from "react";
import "./Navbar.css";
import SearchIcon from "@mui/icons-material/Search";
import { Avatar, Badge, IconButton } from "@mui/material";
import { pink } from "@mui/material/colors";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { Person } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
export const Navbar = () => {
  const {auth,cart}=useSelector(store=>store)
  const navigate = useNavigate();

  const handleAvatarClick=()=>{
    if(auth.user?.role=="ROLE_CUSTOMER"){
      navigate("/my-proflie")
    }
    else{
      navigate("/admin/stores")
    }
  }
  return (
    <div className="px-5 sticky top-0 z-50 py-[.8rem] bg-[#e91e63] lg:px-20 flex justify-between">
      <div className="lg:mr-10 cursor-pointer flex items-center">
        <li onClick={()=>navigate("/")} className="logo font-semibold text-gray-300 text-2xl">FASTFOOD</li>
      </div>
      <div className="flex items-center space-x-2 lg:space-x-10">
        <div className="">
          <IconButton>
            <SearchIcon sx={{ fontSize: "1.5rem" }} />
          </IconButton>
        </div>
        <div className="">
          {auth.user ? (
            <Avatar onClick={handleAvatarClick} sx={{ bgcolor: "white", color: pink.A400 }}>
              {auth.user?.fullName[0].toUpperCase()}
              </Avatar>
          ) : (
            <IconButton onClick={() => navigate("/account/login")}>
              <Person />
            </IconButton>
          )}
        </div>
        <div className="">
          <IconButton onClick={()=>navigate("cart")}>
            <Badge color="secondary" badgeContent={cart.cart?.items.length}>
              <ShoppingCartIcon sx={{ fontSize: "1.5rem" }} />
            </Badge>
          </IconButton>
        </div>
      </div>
    </div>
  );
};
