import React, { useEffect, useState } from 'react';
import {
  Box,
  Card,
  TableContainer,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Typography,
  Paper,
  Chip,
  Button,
  Stack,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { getOrders } from '../../componet/State/Order/Action';

const paymentTypes = ['Ti·ªÅn m·∫∑t', 'Chuy·ªÉn kho·∫£n', 'Th·∫ª t√≠n d·ª•ng'];
const paymentStatuses = ['Ch∆∞a thanh to√°n', 'ƒê√£ thanh to√°n'];

// D·ªØ li·ªáu m·∫´u chi ti·∫øt ƒë∆°n h√†ng
const mockOrderDetail = {
  id: 1,
  totalAmount: null,
  orderStatus: 'Ch∆∞a gi·∫£i quy·∫øt',
  createAt: '2024-12-14T14:51:40.990+00:00',
  deliveryAddress: {
    fullName: 'Nguy·ªÖn VƒÉn A',
    phone: '0123456789',
    fullAddress: '123 ƒê∆∞·ªùng ABC, Qu·∫≠n 1, TP.HCM',
  },
  items: [
    {
      id: 2,
      product: {
        name: 'Arm Mecamum 2DoF PS2/Bluetooth Arduino',
        price: 250000,
        images: [
          '//product.hstatic.net/200000469993/product/of_arduino_ps2_-_bo_xe_mecanum.jpg',
        ],
      },
      quantity: 1,
      totalPrice: 250000,
    },
  ],
  paymentType: 0,
  statusPayment: 0,
  totalItem: 1,
  totalPrice: 250000,
};

const Orders = () => {
  const dispatch = useDispatch();
  const { orders, page, size, totalPages, loading, error } = useSelector((state) => state.order);

  const jwt = localStorage.getItem('jwt');
  const [currentPage, setCurrentPage] = useState(0);
  const [visiblePages, setVisiblePages] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null); // L∆∞u ƒë∆°n h√†ng ƒë∆∞·ª£c ch·ªçn
  const [openModal, setOpenModal] = useState(false); // ƒêi·ªÅu khi·ªÉn m·ªü/ƒë√≥ng modal

  useEffect(() => {
    if (jwt) {
      dispatch(getOrders(jwt, currentPage, 10));
    }
  }, [dispatch, jwt, currentPage]);

  useEffect(() => {
    updateVisiblePages();
  }, [currentPage, totalPages]);

  const updateVisiblePages = () => {
    const maxVisible = 3;
    let startPage = Math.max(0, currentPage - 1);
    let endPage = Math.min(totalPages - 1, startPage + maxVisible - 1);

    if (endPage - startPage + 1 < maxVisible) {
      startPage = Math.max(0, endPage - maxVisible + 1);
    }

    const pages = [];
    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    setVisiblePages(pages);
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  const handleOpenModal = (order) => {
    setSelectedOrder(order);
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setSelectedOrder(null);
  };

  return (
    <Box p={3}>
      <Card>
        <Typography variant="h5" sx={{ p: 2, textAlign: 'center' }}>
          üì¶ Qu·∫£n l√Ω ƒê∆°n H√†ng
        </Typography>

        {/* Loading */}
        {loading && (
          <Stack direction="row" justifyContent="center" alignItems="center" sx={{ p: 4 }}>
            <CircularProgress />
          </Stack>
        )}

        {/* Error */}
        {error && (
          <Typography variant="h6" color="error" align="center" sx={{ p: 2 }}>
            ‚ùå {error}
          </Typography>
        )}

        {/* Danh s√°ch ƒë∆°n h√†ng */}
        {!loading && !error && (
          <>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>#</TableCell>
                    <TableCell>Tr·∫°ng th√°i</TableCell>
                    <TableCell>Ng√†y t·∫°o</TableCell>
                    <TableCell>Ph∆∞∆°ng th·ª©c thanh to√°n</TableCell>
                    <TableCell>Tr·∫°ng th√°i thanh to√°n</TableCell>
                    <TableCell>T·ªïng s·ªë m√≥n</TableCell>
                    <TableCell>T·ªïng gi√°</TableCell>
                    <TableCell>Chi ti·∫øt</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {orders.map((order) => (
                    <TableRow key={order.id} hover>
                      <TableCell>{order.id}</TableCell>
                      <TableCell>
                        <Chip label={order.orderStatus} color="warning" />
                      </TableCell>
                      <TableCell>{new Date(order.createAt).toLocaleString()}</TableCell>
                      <TableCell>{paymentTypes[order.paymentType]}</TableCell>
                      <TableCell>{paymentStatuses[order.statusPayment]}</TableCell>
                      <TableCell>{order.totalItem}</TableCell>
                      <TableCell>{order.totalPrice.toLocaleString()} VND</TableCell>
                      <TableCell>
                        <Button variant="outlined" onClick={() => handleOpenModal(mockOrderDetail)}>
                          Xem
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            {/* Ph√¢n trang */}
            <Stack direction="row" justifyContent="center" spacing={1} p={2}>
              <Button onClick={() => handlePageChange(currentPage - 1)}>‚Üê</Button>
              {visiblePages.map((pageNumber) => (
                <Button
                  key={pageNumber}
                  variant={currentPage === pageNumber ? 'contained' : 'outlined'}
                  onClick={() => handlePageChange(pageNumber)}
                >
                  {pageNumber + 1}
                </Button>
              ))}
              <Button onClick={() => handlePageChange(currentPage + 1)}>‚Üí</Button>
            </Stack>
          </>
        )}

        {/* Modal Chi Ti·∫øt ƒê∆°n H√†ng */}
        <Dialog open={openModal} onClose={handleCloseModal} maxWidth="md" fullWidth>
          <DialogTitle>Chi Ti·∫øt ƒê∆°n H√†ng</DialogTitle>
          <DialogContent>
            {selectedOrder && (
              <>
                <Typography>ID: {selectedOrder.id}</Typography>
                <Typography>Tr·∫°ng th√°i: {selectedOrder.orderStatus}</Typography>
                <Typography>Ng√†y t·∫°o: {selectedOrder.createAt}</Typography>
                <Typography>ƒê·ªãa ch·ªâ: {selectedOrder.deliveryAddress.fullAddress}</Typography>
                <Typography>
                  S·∫£n ph·∫©m: {selectedOrder.items[0].product.name} - {selectedOrder.items[0].product.price} VND
                </Typography>
              </>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseModal}>ƒê√≥ng</Button>
          </DialogActions>
        </Dialog>
      </Card>
    </Box>
  );
};

export default Orders;
