import React, { useEffect, useState } from "react";
import {
  Box,
  Card,
  TableContainer,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Typography,
  Paper,
  Chip,
  Button,
  Stack,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  Menu,
  MenuItem,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import {
  getOrders,
  getOrderById,
  updateOrderStatus,
} from "../../componet/State/Order/Action";

const paymentTypes = ["Ti·ªÅn m·∫∑t", "Chuy·ªÉn kho·∫£n", "Th·∫ª t√≠n d·ª•ng", "V√≠ ƒëi·ªán t·ª≠"];
const paymentStatuses = ["Ch∆∞a thanh to√°n", "ƒê√£ thanh to√°n"];
const statusOptions = [
  "Ch∆∞a gi·∫£i quy·∫øt",
  "ƒêang giao h√†ng",
  "ƒê√£ nh·∫≠n h√†ng",
  "ƒê√£ ho√†n th√†nh ƒë∆°n",
];

const Orders = () => {
  const dispatch = useDispatch();
  const {
    orders,
    page,
    size,
    totalPages,
    loading,
    error,
    selectedOrder,
    orderLoading,
    updating,
    updateSuccess,
    updateError,
  } = useSelector((state) => state.order);

  const jwt = localStorage.getItem("jwt");
  const [currentPage, setCurrentPage] = useState(0);
  const [visiblePages, setVisiblePages] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);

  useEffect(() => {
    if (jwt) {
      dispatch(getOrders(jwt, currentPage, 10));
    }
  }, [dispatch, jwt, currentPage]);

  useEffect(() => {
    updateVisiblePages();
  }, [currentPage, totalPages]);

  useEffect(() => {
    if (updateSuccess) {
      alert("C·∫≠p nh·∫≠t tr·∫°ng th√°i th√†nh c√¥ng!");
      setAnchorEl(null);
    }
    if (updateError) {
      alert(`L·ªói: ${updateError}`);
    }
  }, [updateSuccess, updateError]);

  const updateVisiblePages = () => {
    const maxVisible = 3;
    let startPage = Math.max(0, currentPage - 1);
    let endPage = Math.min(totalPages - 1, startPage + maxVisible - 1);

    if (endPage - startPage + 1 < maxVisible) {
      startPage = Math.max(0, endPage - maxVisible + 1);
    }

    const pages = [];
    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    setVisiblePages(pages);
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  const handleOpenModal = (orderId) => {
    dispatch(getOrderById(jwt, orderId)); // G·ªçi API l·∫•y chi ti·∫øt ƒë∆°n h√†ng
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
  };

  const handleStatusClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleStatusSelect = (newStatus) => {
    if (selectedOrder) {
      dispatch(updateOrderStatus(jwt, selectedOrder.id, newStatus));
      setAnchorEl(null);
    }
  };

  return (
    <Box p={3}>
      <Card>
        <Typography variant="h5" sx={{ p: 2, textAlign: "center" }}>
          üì¶ Qu·∫£n l√Ω ƒê∆°n H√†ng
        </Typography>

        {/* Loading */}
        {loading && (
          <Stack
            direction="row"
            justifyContent="center"
            alignItems="center"
            sx={{ p: 4 }}
          >
            <CircularProgress />
          </Stack>
        )}

        {/* Error */}
        {error && (
          <Typography variant="h6" color="error" align="center" sx={{ p: 2 }}>
            ‚ùå {error}
          </Typography>
        )}

        {/* Danh s√°ch ƒë∆°n h√†ng */}
        {!loading && !error && (
          <>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>#</TableCell>
                    <TableCell>Tr·∫°ng th√°i</TableCell>
                    <TableCell>Ng√†y t·∫°o</TableCell>
                    <TableCell>Ph∆∞∆°ng th·ª©c thanh to√°n</TableCell>
                    <TableCell>Tr·∫°ng th√°i thanh to√°n</TableCell>
                    <TableCell>T·ªïng s·ªë m√≥n</TableCell>
                    <TableCell>T·ªïng gi√°</TableCell>
                    <TableCell>Chi ti·∫øt</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {orders.map((order) => (
                    <TableRow key={order.id} hover>
                      <TableCell>{order.id}</TableCell>
                      <TableCell>
                        <Chip label={order.orderStatus} color="primary" />
                      </TableCell>
                      <TableCell>
                        {new Date(order.createAt).toLocaleString()}
                      </TableCell>
                      <TableCell>{paymentTypes[order.paymentType]}</TableCell>
                      <TableCell>
                        {paymentStatuses[order.statusPayment]}
                      </TableCell>
                      <TableCell>{order.totalItem}</TableCell>
                      <TableCell>
                        {order.totalPrice.toLocaleString()} VND
                      </TableCell>
                      <TableCell>
                        <Button
                          variant="outlined"
                          onClick={() => handleOpenModal(order.id)}
                        >
                          Xem
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            {/* Ph√¢n trang */}
            <Stack direction="row" justifyContent="center" spacing={1} p={2}>
              <Button onClick={() => handlePageChange(currentPage - 1)}>
                ‚Üê
              </Button>
              {visiblePages.map((pageNumber) => (
                <Button
                  key={pageNumber}
                  variant={
                    currentPage === pageNumber ? "contained" : "outlined"
                  }
                  onClick={() => handlePageChange(pageNumber)}
                >
                  {pageNumber + 1}
                </Button>
              ))}
              <Button onClick={() => handlePageChange(currentPage + 1)}>
                ‚Üí
              </Button>
            </Stack>
          </>
        )}

        {/* Modal Chi Ti·∫øt ƒê∆°n H√†ng */}
        <Dialog
