import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Button,
  Checkbox,
  FormControlLabel,
  FormGroup,
} from "@mui/material";
import React, { useState } from "react";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { connect_ingredients } from "../Connect_Category_Ingredients/connect_ingredients";
import { useDispatch } from "react-redux";
import { addItemToCart } from "../State/Cart/Action";
// const ingredients = [
//   {
//     category: "Nuts & seeds",
//     ingredients: "Cashews",
//   },
//   {
//     category: "Protein",
//     ingredients: "rotein",
//   },
//   {
//     category: "Protein",
//     ingredients: "Bacon strips",
//   },
// ];
const demo = [
  {
    category: "Nuts & seeds",
    ingredients: ["Cashews"],
  },
  {
    category: "Protein",
    ingredients: ["rotein", "Bacon strips"],
  },
];
const MenuCard = ({item}) => {
  const [selectedIngredients,setSelectedIngredients]=useState([])
  const dispatch=useDispatch();
  const handleCheckBoxChange = (itemName) => {
    console.log("value",itemName);
    if(selectedIngredients.includes(itemName)){
      setSelectedIngredients(selectedIngredients.filter((item) => item!==itemName));
    }else{
      setSelectedIngredients([...selectedIngredients, itemName]);
    };
  };
  const handleAddToCart=(e)=>{
    e.preventDefault()
    const reqData = {
      token:localStorage.getItem("jwt"),
      cartItem:{
      foodId:item.id,
      quantity:1,
      ingredients:selectedIngredients,
      },
    };
    dispatch(addItemToCart(reqData))
    console.log("req data",reqData)
  };


  return (
    <Accordion>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-controls="panel1-content"
        id="panel1-header"
      >
        <div className="lg:flex items-center justify-between">
          <div className="lg:flex items-center lg:gap-5">
            <img
              className="w-[7rem] h-[7rem] object-cover"
              src={item.images}
              alt=""
            />
            <div className="space-y-1 lg:space-y-5 lg:max-w-2xl">
              <p className="font-semiblod text-xl"> {item.name}</p>
              <p>{item.price}đ</p>
              <p className="text-gray-400">{item.description}</p>
            </div>
          </div>
        </div>
      </AccordionSummary>
      <AccordionDetails>
        <form onSubmit={handleAddToCart}>
          <div className="flex gap-5 flex-wrap ">
            {Object.keys(connect_ingredients(item.ingredients)).map((category) => (
              <div>
                <p className=""> {category}</p>
                <FormGroup>
                  {connect_ingredients(item.ingredients)[category].map((item) => (
                    <FormControlLabel key={item.id}
                      control={
                        <Checkbox onChange={() => handleCheckBoxChange(item.name)} />
                      }
                      label={item.name}
                    />
                  ))}
                </FormGroup>
              </div>
            ))}
          </div>
          <div className="pt-5">
            <Button  variant="contained" disabled={false} type="submit">
              {true ? "Add to Cart " : "Out of Stock "}
            </Button>
          </div>
        </form>
      </AccordionDetails>
    </Accordion>
  );
};

export default MenuCard;
