import React, { useEffect, useState } from "react";
import {
    Box,
    Card,
    CardHeader,
    IconButton,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    TextField,
} from "@mui/material";
import CreateIcon from "@mui/icons-material/Create";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch, useSelector } from "react-redux";
import { deleteCategory, getCategory } from "../../componet/State/CategoryTest/Action";

export default function CategoryParentTable() {
    const [openDialog, setOpenDialog] = useState(false); // Kiá»ƒm soÃ¡t Dialog má»Ÿ/Ä‘Ã³ng
    const [isEditMode, setIsEditMode] = useState(false); // Cháº¿ Ä‘á»™ chá»‰nh sá»­a
    const [editingCategory, setEditingCategory] = useState({
        id: null,
        name: "",
    }); // Dá»¯ liá»‡u danh má»¥c

    const dispatch = useDispatch();
    const { restaurant, category } = useSelector((store) => store);
    const jwt = localStorage.getItem("jwt");

    useEffect(() => {
        dispatch(getCategory({ id: restaurant.usersRestaurant.id, jwt }));
    }, [dispatch, restaurant.usersRestaurant.id, jwt]);

    // ðŸ“Œ Má»Ÿ Dialog ThÃªm/Sá»­a
    const handleOpenDialog = (item = null) => {
        if (item) {
            setEditingCategory({ id: item.id, name: item.name });
            setIsEditMode(true);
        } else {
            setEditingCategory({ id: null, name: "" });
            setIsEditMode(false);
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setEditingCategory({ id: null, name: "" });
    };

    // ðŸ“Œ Xá»­ lÃ½ ThÃªm/Sá»­a Danh Má»¥c
    const handleAddOrUpdateCategory = () => {
        if (isEditMode) {
            console.log("Cáº­p nháº­t danh má»¥c:", editingCategory);
            // ThÃªm action cáº­p nháº­t danh má»¥c á»Ÿ Ä‘Ã¢y
        } else {
            console.log("ThÃªm danh má»¥c:", editingCategory);
            // ThÃªm action táº¡o danh má»¥c á»Ÿ Ä‘Ã¢y
        }
        handleCloseDialog();
    };

    // ðŸ“Œ XÃ³a Danh Má»¥c
    const handleDelete = (id) => {
        if (window.confirm("Báº¡n cÃ³ cháº¯c cháº¯n muá»‘n xÃ³a danh má»¥c nÃ y khÃ´ng?")) {
            dispatch(deleteCategory({ id, jwt }));
        }
    };

    return (
        <div>
            <Box>
                <Card className="mt-1">
                    <CardHeader
                        action={
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => handleOpenDialog()}
                            >
                                ThÃªm Má»›i
                            </Button>
                        }
                        title={"Danh má»¥c"}
                        sx={{ pt: 2, alignItems: "center" }}
                    />

                    {/* Báº£ng Danh Má»¥c */}
                    <TableContainer component={Paper}>
                        <Table sx={{ minHeight: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell align="left">ID</TableCell>
                                    <TableCell align="left">TÃªn</TableCell>
                                    <TableCell align="center">HÃ nh Ä‘á»™ng</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {category.category.map((item) => (
                                    <TableRow
                                        key={item.id}
                                        sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                                    >
                                        <TableCell component="th" scope="row">
                                            {item.id}
                                        </TableCell>
                                        <TableCell align="left">{item.name}</TableCell>
                                        <TableCell align="center">
                                            {/* NÃºt Sá»­a */}
                                            <IconButton
                                                color="primary"
                                                onClick={() => handleOpenDialog(item)}
                                            >
                                                <EditIcon />
                                            </IconButton>
                                            {/* NÃºt XÃ³a */}
                                            <IconButton
                                                color="error"
                                                onClick={() => handleDelete(item.id)}
                                            >
                                                <DeleteIcon />
                                            </IconButton>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Card>

                {/* Dialog ThÃªm/Sá»­a Danh Má»¥c */}
                <Dialog open={openDialog} onClose={handleCloseDialog}>
                    <DialogTitle>
                        {isEditMode ? "Chá»‰nh Sá»­a Danh Má»¥c" : "ThÃªm Má»›i Danh Má»¥c"}
                    </DialogTitle>
                    <DialogContent>
                        {/* Input TÃªn Danh Má»¥c */}
                        <TextField
                            label="TÃªn danh má»¥c"
                            fullWidth
                            margin="normal"
                            value={editingCategory.name}
                            onChange={(e) =>
                                setEditingCategory({ ...editingCategory, name: e.target.value })
                            }
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleCloseDialog}>Há»§y</Button>
                        <Button
                            onClick={handleAddOrUpdateCategory}
                            variant="contained"
                            color="primary"
                        >
                            {isEditMode ? "Cáº­p Nháº­t" : "ThÃªm"}
                        </Button>
                    </DialogActions>
                </Dialog>
            </Box>
        </div>
    );
}
