import React, { useEffect, useState } from 'react';
import {
  Box,
  Card,
  TableContainer,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Typography,
  Paper,
  Chip,
  Button,
  Stack,
  CircularProgress,
} from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { getOrders } from '../../actions/orderActions';

const paymentTypes = ['Tiền mặt', 'Chuyển khoản', 'Thẻ tín dụng'];
const paymentStatuses = ['Chưa thanh toán', 'Đã thanh toán'];

const Orders = () => {
  const dispatch = useDispatch();
  const { orders, page, size, totalPages, loading, error } = useSelector((state) => state.orders);

  const jwt = localStorage.getItem('jwt');
  const [currentPage, setCurrentPage] = useState(0);
  const [visiblePages, setVisiblePages] = useState([]);

  useEffect(() => {
    if (jwt) {
      dispatch(getOrders(jwt, currentPage, 10));
    }
  }, [dispatch, jwt, currentPage]);

  useEffect(() => {
    updateVisiblePages();
  }, [currentPage, totalPages]);

  // Cập nhật danh sách trang hiển thị
  const updateVisiblePages = () => {
    const maxVisible = 3; // Giới hạn số trang hiển thị cùng lúc
    let startPage = Math.max(0, currentPage - 1); // Trang bắt đầu (trừ 1 để nằm giữa)
    let endPage = Math.min(totalPages - 1, startPage + maxVisible - 1);

    // Điều chỉnh nếu chạm giới hạn cuối
    if (endPage - startPage + 1 < maxVisible) {
      startPage = Math.max(0, endPage - maxVisible + 1);
    }

    const pages = [];
    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    setVisiblePages(pages);
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <Box p={3}>
      <Card>
        <Typography variant="h5" sx={{ p: 2, textAlign: 'center' }}>
          📦 Quản lý Đơn Hàng
        </Typography>

        {/* Loading */}
        {loading && (
          <Stack direction="row" justifyContent="center" alignItems="center" sx={{ p: 4 }}>
            <CircularProgress />
          </Stack>
        )}

        {/* Error */}
        {error && (
          <Typography variant="h6" color="error" align="center" sx={{ p: 2 }}>
            ❌ {error}
          </Typography>
        )}

        {/* Danh sách đơn hàng */}
        {!loading && !error && (
          <>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>#</TableCell>
                    <TableCell>Trạng thái</TableCell>
                    <TableCell>Ngày tạo</TableCell>
                    <TableCell>Phương thức thanh toán</TableCell>
                    <TableCell>Trạng thái thanh toán</TableCell>
                    <TableCell>Tổng số món</TableCell>
                    <TableCell>Tổng giá</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {orders.map((order) => (
                    <TableRow key={order.id} hover>
                      <TableCell>{order.id}</TableCell>
                      <TableCell>
                        <Chip
                          label={order.orderStatus}
                          color={
                            order.orderStatus === 'Chưa giải quyết'
                              ? 'warning'
                              : order.orderStatus === 'Đang giao hàng'
                              ? 'primary'
                              : 'success'
                          }
                        />
                      </TableCell>
                      <TableCell>
                        {new Date(order.createAt).toLocaleString()}
                      </TableCell>
                      <TableCell>{paymentTypes[order.paymentType]}</TableCell>
                      <TableCell>{paymentStatuses[order.statusPayment]}</TableCell>
                      <TableCell>{order.totalItem}</TableCell>
                      <TableCell>
                        {order.totalPrice.toLocaleString()} VND
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            {/* Phân trang động */}
            <Stack direction="row" justifyContent="center" alignItems="center" spacing={1} p={2}>
              <Button
                variant="contained"
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 0}
              >
                ←
              </Button>

              {visiblePages.map((pageNumber) => (
                <Button
                  key={pageNumber}
                  variant={currentPage === pageNumber ? 'contained' : 'outlined'}
                  onClick={() => handlePageChange(pageNumber)}
                >
                  {pageNumber + 1}
                </Button>
              ))}

              <Button
                variant="contained"
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages - 1}
              >
                →
              </Button>
            </Stack>

            <Typography align="center" sx={{ pb: 2 }}>
              Trang {currentPage + 1} / {totalPages}
            </Typography>
          </>
        )}
      </Card>
    </Box>
  );
};

export default Orders;
