import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Pagination,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    TextField,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
} from '@mui/material';
import { createBlog, deleteBlog, getBlogsPaginated, updateBlog } from '../../componet/State/Blog/Action';

const BlogManagement = () => {
    const dispatch = useDispatch();
    const { blogs, loading, error, currentPage, totalPages } = useSelector((state) => state.blog);

    const [page, setPage] = useState(1); // Trang hiện tại (bắt đầu từ 1)
    const [openDialog, setOpenDialog] = useState(false);
    const [isEditMode, setIsEditMode] = useState(false); // Chế độ chỉnh sửa
    const [editingBlog, setEditingBlog] = useState({
        id: null,
        title: '',
        content: '',
        category: '',
    });

    // Fetch Blogs mỗi khi page thay đổi
    useEffect(() => {
        dispatch(getBlogsPaginated(page - 1, 5)); // Backend page bắt đầu từ 0
    }, [dispatch, page]);

    const handlePageChange = (event, value) => {
        setPage(value);
    };

    // Xử lý Thêm Mới Blog
    const handleAddBlog = () => {
        if (isEditMode) {
            dispatch(updateBlog(editingBlog.id, editingBlog));
        } else {
            dispatch(createBlog(editingBlog));
        }
        setEditingBlog({ id: null, title: '', content: '', category: '' });
        setOpenDialog(false);
        setIsEditMode(false);
    };

    // Xử lý Mở Dialog Thêm/Sửa Blog
    const handleOpenDialog = (blog = null) => {
        if (blog) {
            setEditingBlog({
                id: blog.id,
                title: blog.title,
                content: blog.content,
                category: blog.category,
            });
            setIsEditMode(true);
        } else {
            setEditingBlog({ title: '', content: '', category: '' });
            setIsEditMode(false);
        }
        setOpenDialog(true);
    };

    // Xử lý Xóa Blog
    const handleDeleteBlog = (id) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa bài viết này?')) {
            dispatch(deleteBlog(id));
        }
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h1 style={{ textAlign: 'center' }}>Quản lý Blog</h1>

            {/* Nút Thêm Mới */}
            <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '1rem',
            }}>
                <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleOpenDialog()}
                >
                    Thêm Mới
                </Button>
            </div>

            {/* Bảng Blog */}
            <TableContainer component={Paper} style={{ marginTop: '1rem' }}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell><strong>Tiêu đề</strong></TableCell>
                            <TableCell><strong>Danh mục</strong></TableCell>
                            <TableCell><strong>Ngày tạo</strong></TableCell>
                            <TableCell><strong>Hành động</strong></TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {blogs.map((blog) => (
                            <TableRow key={blog.id}>
                                <TableCell>{blog.title}</TableCell>
                                <TableCell>{blog.category}</TableCell>
                                <TableCell>{blog.createdAt}</TableCell>
                                <TableCell>
                                    <Button
                                        variant="outlined"
                                        color="primary"
                                        size="small"
                                        style={{ marginRight: '0.5rem' }}
                                        onClick={() => handleOpenDialog(blog)}
                                    >
                                        Sửa
                                    </Button>
                                    <Button
                                        variant="outlined"
                                        color="secondary"
                                        size="small"
                                        style={{ marginRight: '0.5rem' }}
                                        onClick={() => handleDeleteBlog(blog.id)}
                                    >
                                        Xóa
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {/* Dialog Thêm/Sửa Blog */}
            <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
                <DialogTitle>{isEditMode ? 'Chỉnh Sửa Blog' : 'Thêm Mới Blog'}</DialogTitle>
                <DialogContent>
                    <TextField label="Tiêu đề" fullWidth value={editingBlog.title} onChange={(e) => setEditingBlog({ ...editingBlog, title: e.target.value })} />
                    <TextField label="Nội dung" fullWidth multiline rows={4} value={editingBlog.content} onChange={(e) => setEditingBlog({ ...editingBlog, content: e.target.value })} />
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
                    <Button onClick={handleAddBlog} variant="contained" color="primary">
                        {isEditMode ? 'Cập Nhật' : 'Thêm'}
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default BlogManagement;
