import React, { useState, useEffect } from 'react';

const Orderss = () => {
  const [orders, setOrders] = useState([]); // Danh sách đơn hàng
  const [currentPage, setCurrentPage] = useState(0); // Trang hiện tại
  const [totalPages, setTotalPages] = useState(0); // Tổng số trang
  const [pageSize, setPageSize] = useState(5); // Số phần tử mỗi trang

  useEffect(() => {
    fetchOrders();
  }, [currentPage, pageSize]);

  const fetchOrders = async () => {
    try {
      const response = await fetch(
        `/api/orders?page=${currentPage}&size=${pageSize}&sort=createAt,desc`
      );
      const data = await response.json();
      setOrders(data.content);
      setTotalPages(data.totalPages);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handlePageChange = (page) => {
    if (page >= 0 && page < totalPages) {
      setCurrentPage(page);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Danh Sách Đơn Hàng</h2>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tổng Tiền</th>
            <th>Trạng Thái</th>
            <th>Ngày Tạo</th>
            <th>Thanh Toán</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.totalAmount}</td>
              <td>{order.orderStatus}</td>
              <td>{new Date(order.createAt).toLocaleDateString()}</td>
              <td>{order.statusPayment === 1 ? 'Đã Thanh Toán' : 'Chưa Thanh Toán'}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Phân Trang */}
      <div style={styles.pagination}>
        <button
          style={styles.pageButton}
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 0}
        >
          Trước
        </button>
        <span style={styles.pageInfo}>
          Trang {currentPage + 1} / {totalPages}
        </span>
        <button
          style={styles.pageButton}
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage + 1 === totalPages}
        >
          Tiếp
        </button>
      </div>
    </div>
  );
};

// Styles (Inline Style)
const styles = {
  container: {
    margin: '20px auto',
    padding: '20px',
    maxWidth: '800px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9'
  },
  header: {
    textAlign: 'center',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginBottom: '20px'
  },
  pagination: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '10px'
  },
  pageButton: {
    padding: '5px 10px',
    cursor: 'pointer',
    border: '1px solid #ccc',
    borderRadius: '4px',
    backgroundColor: '#fff'
  },
  pageInfo: {
    fontWeight: 'bold'
  },
  tableHeader: {
    backgroundColor: '#eaeaea'
  }
};

export default Orderss;
