import {
    Box,
    Card,
    CardHeader,
    IconButton,
    Modal,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
  } from "@mui/material";
  import React, { useEffect } from "react";
  import CreateIcon from "@mui/icons-material/Create";
  import { useDispatch, useSelector } from "react-redux";
  import CreateCategoryParent from "./CreateCategoryParent";
  import { getCategory } from "../../componet/State/CategoryTest/Action";
  import $ from 'jquery';

  
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  
  export default function CategoryParentTable() {
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    const dispatch = useDispatch();
    const { restaurant, category } = useSelector((store) => store);
    const jwt = localStorage.getItem("jwt");
  
    useEffect(() => {
      dispatch(getCategory({ id: restaurant.usersRestaurant.id, jwt }));
    }, []);
  
    useEffect(() => {
      $(document).ready(function() {
        $('#categoryTable').DataTable();
      });
    }, [category]);
  
    return (
      <div>
        <Box>
          <Card className="mt-1">
            <CardHeader
              action={
                <IconButton onClick={handleOpen} aria-label="settings">
                  <CreateIcon />
                </IconButton>
              }
              title={"Danh mục thành phần"}
              sx={{ pt: 2, alignItems: "center" }}
            />
  
            <TableContainer component={Paper}>
              <Table id="categoryTable" aria-label="simple table">
                <TableHead>
                  <TableRow>
                    <TableCell align="left">ID</TableCell>
                    <TableCell align="left">Tên</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {category.category.map((item) => (
                    <TableRow key={item.name} sx={{ "&:last-child td, &:last-child th": { border: 0 } }}>
                      <TableCell component="th" scope="row">{item.id}</TableCell>
                      <TableCell align="left">{item.name}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Card>
          <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
          >
            <Box sx={style}>
              <CreateCategoryParent />
            </Box>
          </Modal>
        </Box>
      </div>
    );
  }
