import React, { useEffect, useState } from "react";
import {
  Box,
  Card,
  CardHeader,
  IconButton,
  Modal,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";
import CreateIcon from "@mui/icons-material/Create";
import { useDispatch, useSelector } from "react-redux";
import CreateCategoryParent from "./CreateCategoryParent";
import { getCategory } from "../../componet/State/CategoryTest/Action";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: "60%", // Tăng kích thước modal
  maxWidth: "800px", // Giới hạn chiều rộng tối đa
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4, // Tăng padding
  borderRadius: "8px", // Làm mềm góc cạnh
  overflowY: "auto", // Cho phép cuộn nếu nội dung quá dài
  maxHeight: "90vh", // Giới hạn chiều cao modal
};

export default function CategoryParentTable() {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const dispatch = useDispatch();
  const { restaurant, category } = useSelector((store) => store);
  const jwt = localStorage.getItem("jwt");

  useEffect(() => {
    dispatch(getCategory({ id: restaurant.usersRestaurant.id, jwt }));
  }, [dispatch, restaurant.usersRestaurant.id, jwt]);

  return (
    <div>
      <Box>
        <Card className="mt-1">
          <CardHeader
            action={
              <IconButton onClick={handleOpen} aria-label="settings">
                <CreateIcon />
              </IconButton>
            }
            title={"Danh mục thành phần"}
            sx={{ pt: 2, alignItems: "center" }}
          />

          {/* Bảng Danh Mục */}
          <TableContainer component={Paper}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell align="left">ID</TableCell>
                  <TableCell align="left">Tên</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {category.category.map((item) => (
                  <TableRow
                    key={item.id}
                    sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                  >
                    <TableCell component="th" scope="row">
                      {item.id}
                    </TableCell>
                    <TableCell align="left">{item.name}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Card>

        {/* Modal Thêm Danh Mục */}
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <CreateCategoryParent />
          </Box>
        </Modal>
      </Box>
    </div>
  );
}
