    if (jwt) {
      dispatch(getTotalPriceOrders(jwt)); // G·ªçi action v·ªõi JWT
    } else {
      console.error('JWT not found in localStorage');
    }
  }, [dispatch]);

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        üìä Th·ªëng K√™
