        <Grid item xs={12} md={6} lg={3}>
          <Card>
            <CardContent>
              <Typography variant="h6">üíµ T·ªïng Doanh Thu</Typography>
              <Typography variant="h4">
                {totalRevenue ? totalRevenue.toLocaleString() : 0} VNƒê
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* C√°c √¥ kh√°c */}
        <Grid item xs={12} md={6} lg={3}>
          <Card>
            <CardContent>
              <Typography variant="h6">üì¶ T·ªïng ƒê∆°n H√†ng</Typography>
              <Typography variant="h4">450</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={6} lg={3}>
          <Card>
            <CardContent>
              <Typography variant="h6">üë• T·ªïng Kh√°ch H√†ng</Typography>
