import React, { useState } from 'react';
import {
  Button,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from '@mui/material';

const initialBlogs = [
  {
    id: 1,
    title: 'Bài viết đầu tiên',
    content: 'Nội dung bài viết đầu tiên',
    category: 'Tài liệu học tập',
    createdAt: '2024-01-05',
    updatedAt: '2024-01-05',
  },
  {
    id: 2,
    title: 'Bài viết thứ hai',
    content: 'Nội dung bài viết thứ hai',
    category: 'Hướng dẫn thực hành',
    createdAt: '2024-01-04',
    updatedAt: '2024-01-05',
  },
];

const BlogManagement = () => {
  const [blogs, setBlogs] = useState(initialBlogs);
  const [openDialog, setOpenDialog] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);
  const [selectedBlog, setSelectedBlog] = useState(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);

  const handleAddOrEditBlog = () => {
    if (editingBlog.id) {
      setBlogs(blogs.map(blog => (blog.id === editingBlog.id ? editingBlog : blog)));
    } else {
      setBlogs([...blogs, { ...editingBlog, id: Date.now(), createdAt: new Date().toISOString() }]);
    }
    setEditingBlog(null);
    setOpenDialog(false);
  };

  const handleDeleteBlog = (id) => {
    if (window.confirm('Bạn có chắc chắn muốn xóa bài viết này?')) {
      setBlogs(blogs.filter(blog => blog.id !== id));
    }
  };

  const handleViewDetails = (blog) => {
    setSelectedBlog(blog);
    setOpenDetailDialog(true);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1 style={{ textAlign: 'center' }}>Quản lý Blog</h1>
      <Button variant="contained" color="primary" onClick={() => { setEditingBlog({}); setOpenDialog(true); }}>
        Thêm Blog
      </Button>
      <TableContainer component={Paper} style={{ marginTop: '1rem' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Tiêu đề</TableCell>
              <TableCell>Danh mục</TableCell>
              <TableCell>Ngày tạo</TableCell>
              <TableCell>Hành động</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {blogs.map(blog => (
              <TableRow key={blog.id}>
                <TableCell>{blog.title}</TableCell>
                <TableCell>{blog.category}</TableCell>
                <TableCell>{blog.createdAt}</TableCell>
                <TableCell>
                  <Button variant="outlined" color="primary" onClick={() => { setEditingBlog(blog); setOpenDialog(true); }}>Sửa</Button>
                  <Button variant="outlined" color="secondary" onClick={() => handleDeleteBlog(blog.id)} style={{ marginLeft: '0.5rem' }}>Xóa</Button>
                  <Button variant="outlined" color="info" onClick={() => handleViewDetails(blog)} style={{ marginLeft: '0.5rem' }}>Chi tiết</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog thêm/sửa Blog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>{editingBlog?.id ? 'Chỉnh sửa Blog' : 'Thêm Blog'}</DialogTitle>
        <DialogContent>
          <TextField
            label="Tiêu đề"
            fullWidth
            margin="normal"
            value={editingBlog?.title || ''}
            onChange={(e) => setEditingBlog({ ...editingBlog, title: e.target.value })}
          />
          <TextField
            label="Nội dung"
            fullWidth
            margin="normal"
            multiline
            rows={4}
            value={editingBlog?.content || ''}
            onChange={(e) => setEditingBlog({ ...editingBlog, content: e.target.value })}
          />
          <FormControl fullWidth margin="normal">
            <InputLabel>Danh mục</InputLabel>
            <Select
              value={editingBlog?.category || ''}
              onChange={(e) => setEditingBlog({ ...editingBlog, category: e.target.value })}
              label="Danh mục"
            >
              <MenuItem value="Tài liệu học tập">Tài liệu học tập</MenuItem>
              <MenuItem value="Hướng dẫn thực hành">Hướng dẫn thực hành</MenuItem>
              <MenuItem value="Khác">Khác</MenuItem>
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
          <Button onClick={handleAddOrEditBlog} variant="contained" color="primary">
            {editingBlog?.id ? 'Cập nhật' : 'Thêm'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog Chi Tiết Blog */}
      <Dialog open={openDetailDialog} onClose={() => setOpenDetailDialog(false)}>
        <DialogTitle>Chi tiết Blog</DialogTitle>
        <DialogContent>
          {selectedBlog && (
            <>
              <p><strong>Tiêu đề:</strong> {selectedBlog.title}</p>
              <p><strong>Nội dung:</strong> {selectedBlog.content}</p>
              <p><strong>Danh mục:</strong> {selectedBlog.category}</p>
              <p><strong>Ngày tạo:</strong> {selectedBlog.createdAt}</p>
              <p><strong>Ngày cập nhật:</strong> {selectedBlog.updatedAt}</p>
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDetailDialog(false)}>Đóng</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default BlogManagement;
