import React, { useEffect } from 'react';
import {
  Avatar,
  Box,
  Button,
  Card,
  CardHeader,
  IconButton,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
} from '@mui/material';
import CreateIcon from '@mui/icons-material/Create';
import { Delete } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getCategory } from '../../componet/State/CategoryTest/Action';

export default function CategoryParentTable() {
  const dispatch = useDispatch();
  const jwt = localStorage.getItem("jwt");
  const { restaurant, category } = useSelector((store) => store);
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(getCategory({
      jwt,
      id: restaurant.usersRestaurant.id,
    }));
  }, [dispatch, jwt, restaurant.usersRestaurant.id]);

  return (
    <Box>
      <Card className='mt-1'>
        <CardHeader
          action={
            <IconButton onClick={() => navigate("/admin/restaurants/add-category")} aria-label="settings">
              <CreateIcon />
            </IconButton>
          }
          title={"Danh Mục"}
          sx={{ pt: 2, alignItems: "center" }}
        />
        
        {/* Table Hiển Thị Danh Mục */}
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="left">ID</TableCell>
                <TableCell align="right">Tên Danh Mục</TableCell>
                <TableCell align="right">Mô Tả</TableCell>
                <TableCell align="right">Trạng Thái</TableCell>
                <TableCell align="right">Hành Động</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {category.category.map((item) => (
                <TableRow key={item.id} sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                  <TableCell component="th" scope="row">
                    {item.id}
                  </TableCell>
                  <TableCell align="right">{item.name}</TableCell>
                  <TableCell align="right">{item.description || "Không có mô tả"}</TableCell>
                  <TableCell align="right">
                    <Button
                      variant="contained"
                      style={{ backgroundColor: item.active ? 'green' : 'red' }}
                    >
                      {item.active ? "Hoạt động" : "Ngừng hoạt động"}
                    </Button>
                  </TableCell>
                  <TableCell align="right">
                    <IconButton color="primary">
                      <Delete />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Card>
    </Box>
  );
}
