import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Pagination,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import { createBlog, getBlogsPaginated } from '../../componet/State/Blog/Action';

const BlogManagement = () => {
    const dispatch = useDispatch();
    const { blogs, loading, error, currentPage, totalPages } = useSelector((state) => state.blog);

    const [page, setPage] = useState(1); // Trang hiện tại (bắt đầu từ 1)
    const [openDialog, setOpenDialog] = useState(false);
    const [editingBlog, setEditingBlog] = useState({
        title: '',
        content: '',
        category: '',
    });

    // Fetch Blogs mỗi khi page thay đổi
    useEffect(() => {
        dispatch(getBlogsPaginated(page - 1, 5)); // Backend page bắt đầu từ 0
    }, [dispatch, page]);

    const handlePageChange = (event, value) => {
        setPage(value);
    };

    // Xử lý Thêm Mới Blog
    const handleAddBlog = () => {
        dispatch(createBlog(editingBlog));
        setEditingBlog({ title: '', content: '', category: '' });
        setOpenDialog(false);
    };

    // Xử lý Mở Dialog Thêm Mới
    const handleOpenDialog = () => {
        setEditingBlog({ title: '', content: '', category: '' });
        setOpenDialog(true);
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h1 style={{ textAlign: 'center' }}>Quản lý Blog</h1>
            
            {/* Nút Thêm Mới */}
            <div style={{
                display: 'flex',
                justifyContent: 'flex-start',
                marginBottom: '1rem',
            }}>
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleOpenDialog}
                >
                    Thêm Mới
                </Button>
            </div>

            {/* Bảng Blog */}
            <TableContainer component={Paper} style={{ marginTop: '1rem' }}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell><strong>Tiêu đề</strong></TableCell>
                            <TableCell><strong>Danh mục</strong></TableCell>
                            <TableCell><strong>Ngày tạo</strong></TableCell>
                            <TableCell><strong>Hành động</strong></TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {blogs.map((blog) => (
                            <TableRow key={blog.id}>
                                <TableCell>{blog.title}</TableCell>
                                <TableCell>{blog.category}</TableCell>
                                <TableCell>{blog.createdAt}</TableCell>
                                <TableCell>
                                    <Button 
                                        variant="outlined" 
                                        color="primary" 
                                        size="small"
                                        style={{ marginRight: '0.5rem' }}
                                    >
                                        Sửa
                                    </Button>
                                    <Button 
                                        variant="outlined" 
                                        color="secondary" 
                                        size="small"
                                        style={{ marginRight: '0.5rem' }}
                                    >
                                        Xóa
                                    </Button>
                                    <Button 
                                        variant="outlined" 
                                        color="info" 
                                        size="small"
                                    >
                                        Chi tiết
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {/* Phân Trang */}
            <Pagination
                count={totalPages}
                page={page}
                onChange={handlePageChange}
                color="primary"
                style={{ marginTop: '1rem', display: 'flex', justifyContent: 'center' }}
            />

            {/* Dialog Thêm Mới Blog */}
            <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
                <DialogTitle>Thêm Mới Blog</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Tiêu đề"
                        fullWidth
                        margin="normal"
                        value={editingBlog.title}
                        onChange={(e) => setEditingBlog({ ...editingBlog, title: e.target.value })}
                    />
                    <TextField
                        label="Nội dung"
                        fullWidth
                        margin="normal"
                        multiline
                        rows={4}
                        value={editingBlog.content}
                        onChange={(e) => setEditingBlog({ ...editingBlog, content: e.target.value })}
                    />
                    <FormControl fullWidth margin="normal">
                        <InputLabel>Danh mục</InputLabel>
                        <Select
                            value={editingBlog.category}
                            onChange={(e) => setEditingBlog({ ...editingBlog, category: e.target.value })}
                            label="Danh mục"
                        >
                            <MenuItem value="Tài liệu học tập">Tài liệu học tập</MenuItem>
                            <MenuItem value="Hướng dẫn thực hành">Hướng dẫn thực hành</MenuItem>
                            <MenuItem value="Khác">Khác</MenuItem>
                        </Select>
                    </FormControl>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
                    <Button
                        onClick={handleAddBlog}
                        variant="contained"
                        color="primary"
                        disabled={loading}
                    >
                        {loading ? 'Đang thêm...' : 'Thêm'}
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Hiển thị lỗi */}
            {error && <p style={{ color: 'red', marginTop: '1rem' }}>Lỗi: {error}</p>}
        </div>
    );
};

export default BlogManagement;
